'use strict';

var chai = require('chai');
var run = require('../src/run');

chai.should();
chai.config.includeStack = true;

describe('promises', function() {
  it('then', function(done) {
    run('ls', '-l').then(function() {
      done();
    });
  });
  it('chained', function(done) {
    run('ls', '-l').then(run('ls', '-l', { cwd: __dirname })).then(function() {
      done();
    });
  });
});