'use strict';

var Path = require('../src/util/path');

var TargetPath = Path.extend({
  abs: function() {
    this.root = process.cwd();
    return Path.prototype.abs.apply(this, arguments);
  }
});

console.log(Path.extend);

var root = new TargetPath();

