'use strict';

var chai = require('chai');
var run = require('../src/run');

chai.should();
chai.config.includeStack = true;

describe('arguments', function() {
  it('command as object', function(done) {
    run({ name: 'ls', arguments: ['-l'] }, function(response) {
      done();
    });
  });
  it('command as string', function(done) {
    run('ls', '-l', function() {
      done();
    });
  });
  it('arguments as array', function(done) {
    run('ls', ['-l'], function() {
      done();
    });
  });
  it('with options', function(done) {
    run('ls', ['-l'], { cwd: __dirname }, function() {
      done();
    });
  });
});